import{_ as t,o as e,c as l,e as d}from"./app-68218236.js";const a="/front-doc/img/basic/css/2-1-1.jpg",i={},n=d('<p>CSS3选择器完全包含了CSS2.1的选择器。</p><figure><img src="'+a+'" alt="2-1-1" tabindex="0" loading="lazy"><figcaption>2-1-1</figcaption></figure><h4 id="_1-基本选择器" tabindex="-1"><a class="header-anchor" href="#_1-基本选择器" aria-hidden="true">#</a> 1. 基本选择器</h4><table><thead><tr><th style="text-align:left;">选择器</th><th style="text-align:left;">类型</th><th style="text-align:left;">功能描述</th></tr></thead><tbody><tr><td style="text-align:left;">*</td><td style="text-align:left;">通配选择器</td><td style="text-align:left;">选择文档中所有HTML元素</td></tr><tr><td style="text-align:left;">E</td><td style="text-align:left;">元素选择器</td><td style="text-align:left;">选择指定标签类型的HTML元素</td></tr><tr><td style="text-align:left;">#id</td><td style="text-align:left;">ID选择器</td><td style="text-align:left;">选择指定ID属性值为“id”的任意类型元素</td></tr><tr><td style="text-align:left;">.class</td><td style="text-align:left;">类选择器</td><td style="text-align:left;">选择指定class属性值为“class”的任意类型的任意多个元素</td></tr><tr><td style="text-align:left;">selector1,selectorN</td><td style="text-align:left;">群组选择器</td><td style="text-align:left;">匹配所有给出的选择器</td></tr></tbody></table><h4 id="_2-层次-组合-选择器" tabindex="-1"><a class="header-anchor" href="#_2-层次-组合-选择器" aria-hidden="true">#</a> 2. 层次（组合）选择器</h4><table><thead><tr><th style="text-align:left;">选择器</th><th style="text-align:left;">类型</th><th style="text-align:left;">功能描述</th></tr></thead><tbody><tr><td style="text-align:left;">E  F</td><td style="text-align:left;">后代选择器（包含选择器）</td><td style="text-align:left;">选择匹配的F元素，且匹配的F元素被包含在匹配的E元素内</td></tr><tr><td style="text-align:left;">E&gt;F</td><td style="text-align:left;">子选择器</td><td style="text-align:left;">选择匹配的F元素，且匹配的F元素是所匹配的E元素的子元素</td></tr><tr><td style="text-align:left;">E+F</td><td style="text-align:left;">相邻兄弟选择器</td><td style="text-align:left;">查找E元素后面的第一个兄弟F节点（E，F 有相同父元素）</td></tr><tr><td style="text-align:left;">E~F</td><td style="text-align:left;">后续兄弟选择器</td><td style="text-align:left;">查找E元素后面的所有兄弟F节点</td></tr></tbody></table><h4 id="_3-属性选择器" tabindex="-1"><a class="header-anchor" href="#_3-属性选择器" aria-hidden="true">#</a> 3. 属性选择器</h4><table><thead><tr><th style="text-align:left;">选择器</th><th style="text-align:left;">功能描述</th></tr></thead><tbody><tr><td style="text-align:left;">[<em>attribute</em>]</td><td style="text-align:left;">选取带有指定属性的元素。</td></tr><tr><td style="text-align:left;">[<em>attribute</em>=<em>value</em>]</td><td style="text-align:left;">选取带有指定属性和值的元素。</td></tr><tr><td style="text-align:left;">[<em>attribute</em>~=<em>value</em>]</td><td style="text-align:left;">选取属性值中包含指定值的元素。该属性有多个值，只要包含该值即可</td></tr><tr><td style="text-align:left;">[<em>attribute</em>|=<em>value</em>]</td><td style="text-align:left;">选取带有指定值开头的属性值的元素，主要用于lang属性</td></tr><tr><td style="text-align:left;">[<em>attribute</em>^=<em>value</em>]</td><td style="text-align:left;">匹配属性值以指定值开头的每个元素。</td></tr><tr><td style="text-align:left;">[<em>attribute</em>$=<em>value</em>]</td><td style="text-align:left;">匹配属性值以指定值结尾的每个元素。</td></tr><tr><td style="text-align:left;">[<em>attribute</em>*=<em>value</em>]</td><td style="text-align:left;">匹配属性值中包含指定值的每个元素。</td></tr></tbody></table><h4 id="_4-伪类选择器" tabindex="-1"><a class="header-anchor" href="#_4-伪类选择器" aria-hidden="true">#</a> 4. 伪类选择器</h4><h5 id="_4-1-动态伪类" tabindex="-1"><a class="header-anchor" href="#_4-1-动态伪类" aria-hidden="true">#</a> 4.1 动态伪类</h5><table><thead><tr><th style="text-align:left;">选择器</th><th style="text-align:left;">类型</th><th style="text-align:left;">功能描述</th></tr></thead><tbody><tr><td style="text-align:left;">E:link</td><td style="text-align:left;">未点击的链接</td><td style="text-align:left;">匹配元素被定义了超链接并未被访问过。常用于链接描点上</td></tr><tr><td style="text-align:left;">E:visited</td><td style="text-align:left;">已被点击的链接</td><td style="text-align:left;">匹配元素被定义了超链接并已被访问过。常用于链接描点上</td></tr><tr><td style="text-align:left;">E:active</td><td style="text-align:left;">点击还未释放</td><td style="text-align:left;">匹配元素被激活。常用于链接描点和按钮上</td></tr><tr><td style="text-align:left;">E:hover</td><td style="text-align:left;">鼠标悬停</td><td style="text-align:left;">用户鼠标停留在元素E上。IE6及以下浏览器仅支持a:hover</td></tr><tr><td style="text-align:left;">E:focus</td><td style="text-align:left;">获得焦点</td><td style="text-align:left;">匹配元素获取焦点</td></tr></tbody></table><h5 id="_4-2-目标伪类" tabindex="-1"><a class="header-anchor" href="#_4-2-目标伪类" aria-hidden="true">#</a> 4.2 目标伪类</h5><table><thead><tr><th style="text-align:left;">选择器</th><th style="text-align:left;">功能描述</th></tr></thead><tbody><tr><td style="text-align:left;">E:target</td><td style="text-align:left;">匹配相关URL指向的E元素</td></tr></tbody></table><h5 id="_4-3-ui状态-界面-伪类" tabindex="-1"><a class="header-anchor" href="#_4-3-ui状态-界面-伪类" aria-hidden="true">#</a> 4.3 UI状态（界面）伪类</h5><table><thead><tr><th style="text-align:left;">选择器</th><th style="text-align:left;">功能描述</th></tr></thead><tbody><tr><td style="text-align:left;">E:checked</td><td style="text-align:left;">匹配表单中被选中的radio或checkbox元素</td></tr><tr><td style="text-align:left;">E:enabled</td><td style="text-align:left;">匹配表单中所有的激活元素</td></tr><tr><td style="text-align:left;">E:disabled</td><td style="text-align:left;">匹配所有禁用的表单元素</td></tr><tr><td style="text-align:left;">E:selection</td><td style="text-align:left;">匹配用户当前选中的元素</td></tr></tbody></table><h5 id="_4-4-结构伪类" tabindex="-1"><a class="header-anchor" href="#_4-4-结构伪类" aria-hidden="true">#</a> 4.4 结构伪类</h5><table><thead><tr><th style="text-align:left;">选择器</th><th style="text-align:left;">功能描述</th></tr></thead><tbody><tr><td style="text-align:left;">E:first-child</td><td style="text-align:left;">作为父元素E的第一个子元素。与E:nth-child(1)等同</td></tr><tr><td style="text-align:left;">E:last-child</td><td style="text-align:left;">作为父元素E的最后一个子元素。与E:nth-last-child(1)等同</td></tr><tr><td style="text-align:left;">E:root</td><td style="text-align:left;">选择匹配元素E所在文档的根元素。在HTML文档中，根元素始终是html</td></tr><tr><td style="text-align:left;">F:nth-child(n)</td><td style="text-align:left;">选择父元素E的第n个子元素。</td></tr><tr><td style="text-align:left;">E :nth-last-child(n)</td><td style="text-align:left;">选择父元素E的倒数第n个子元素</td></tr><tr><td style="text-align:left;">E:nth-of-type(n)</td><td style="text-align:left;">匹配同类型中的第n个同级兄弟元素E</td></tr><tr><td style="text-align:left;">E:nth-last-of-type(n)</td><td style="text-align:left;">匹配同类型中的倒数第n个同级兄弟元素E</td></tr><tr><td style="text-align:left;">E:first-of-type</td><td style="text-align:left;">选择父元素内具有指定类型的第一个E元素，与E:nth-of-type(1)等同</td></tr><tr><td style="text-align:left;">E:last-of-type</td><td style="text-align:left;">选择父元素内具有指定类型的最后一个E元素，与E:nth-last-of-type(1)等同</td></tr><tr><td style="text-align:left;">E:only-child</td><td style="text-align:left;">选择父元素只包含一个子元素，且该子元素匹配E元素</td></tr><tr><td style="text-align:left;">E:only-of-type</td><td style="text-align:left;">选择父元素只包含一个同类型子元素，且该子元素匹配E元素</td></tr><tr><td style="text-align:left;">E:empty</td><td style="text-align:left;">选择没有子元素的元素，而且该元素也不包含任何文本节点</td></tr></tbody></table><h5 id="_4-5-否定-反选-伪类" tabindex="-1"><a class="header-anchor" href="#_4-5-否定-反选-伪类" aria-hidden="true">#</a> 4.5 否定（反选）伪类</h5><table><thead><tr><th style="text-align:left;">选择器</th><th style="text-align:left;">功能描述</th></tr></thead><tbody><tr><td style="text-align:left;">E:not(F)</td><td style="text-align:left;">匹配所有除元素F外的E元素</td></tr></tbody></table><h4 id="_5-伪元素选择器" tabindex="-1"><a class="header-anchor" href="#_5-伪元素选择器" aria-hidden="true">#</a> 5. 伪元素选择器</h4><table><thead><tr><th style="text-align:left;">选择器</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">:first-letter</td><td style="text-align:left;">向文本的第一个字母（英文或中文）添加样式</td></tr><tr><td style="text-align:left;">:first-line</td><td style="text-align:left;">向文本的首行添加样式， 只能与块级元素关联</td></tr><tr><td style="text-align:left;">:before</td><td style="text-align:left;">在元素内部所有子元素之前添加内容</td></tr><tr><td style="text-align:left;">:after</td><td style="text-align:left;">在元素内部所有子元素之后添加内容</td></tr></tbody></table>',21),r=[n];function s(f,y){return e(),l("div",null,r)}const x=t(i,[["render",s],["__file","2-1.概述.html.vue"]]);export{x as default};
